INSERT INTO `email_message` (`email_message_code`, `to_email`, `subject`, `message`, `created_date`, `status`)
VALUES
  ('company_user_account_creation', 'dapo@cottacush.com', 'Courier Plus Account Details', '<!DOCTYPE html><html><head>    <title></title></head><style type=''text/css''>    html {    background-color: #ddd}body {    margin: 10px;    color: #666;    font-size: 16px;    line-height: 22px;    font-family: sans-serif}#all {    background-color: #fff;    padding: 25px;    margin: 0 0 5px}h1, h2, h3, h4, h5, h6 {    color: #333;    margin: 10px 0 15px;    line-height: 1.1}a {    color: #9C27B0}p {    margin-bottom: 10px}header, footer, section {    display: block;}strong, b {    font-weight: bold;    color: #333;}.form-group {    margin-bottom: 10px}label {    font-weight: bold;    color: #333;    margin-bottom: 2px}.login-label {    width: 90px;}.form-control-static {    padding: 3px 0}section, .section {    margin: 8px 0;}.logo {    width: 150px;    display: block;    margin: 0 auto 15px;}.foot-note {    color: #999;    font-size: 14px;    line-height: 1;    text-align: center;}</style><body><div id=''all''>    <div class=''section''>        <p>Dear {{name}},</p>        <p>You have been registered as a user for {{company_name}} on CourierPlus        Track and Trace system.</p>        <p>Please click this <a href=''{{link}}''>link</a> to be able to access the system.</p>        <br/>        <p>As this is your first time of accessing the system, you are required to reset your password using the details below:</p>        <div class=''form-group''><strong class=''login-label''>Username:</strong> {{email}}</div>        <div class=''form-group''><strong class=''login-label''>Password:</strong> {{password}}</div>        <br/>        <p>Do provide us feedback on your experience, advice and area of improvement.<br/>        Kind regards,<br/>        CourierPlus Team.</p>    </div></div><div class=''foot-note''>&copy; 2015 CourierPlus</div></body></html>', NOW(), 1);