INSERT INTO `email_message` (`email_message_code`, `to_email`, `subject`, `message`, `created_date`, `status`)
VALUES
  ('marketing_opportunity', 'dapo@cottacush.com', 'Opportunity to convert Express centre customer to corporate client', 'Hello Marketing Department,<br/>There is an opportunity to convert the below stated detail to a corporate client.<br/>Name: {{firstname}} {{lastname}}<br/>Address: \r\n	{{street1}}\r\n	{{street2}}\r\n	{{city}}, {{state}}\r\n	{{country}}<br/>Contact email : {{email}}<br/>Contact phone number: {{phone}}<br/>Kind regards,<br/>Express Center Management Team', NOW(), 1),
  ('staff_account_creation', 'dapo@cottacush.com', 'Staff Account Creation', '<!DOCTYPE html>\n<html>\n<head>\n	<title></title>\n</head>\n<style type=\'text/css\'>\n	html {background-color: #ddd}\n	body {margin: 10px; color: #666; font-size: 16px; line-height: 22px; font-family: sans-serif}\n	#all {background-color: #fff; padding: 25px; margin: 0 0 5px}\n	h1, h2, h3, h4, h5, h6 {color: #333; margin: 10px 0 15px; line-height: 1.1}\n	a {color: #9C27B0}\n	p {margin-bottom: 10px}\n	header, footer, section {display:block;}\n	strong, b {font-weight: bold; color: #333;}\n	.form-group {margin-bottom: 10px}\n	label {font-weight: bold; color: #333;  margin-bottom: 2px}\n	.login-label {width: 90px;}\n	.form-control-static {padding: 3px 0}\n	section, .section {margin: 8px 0;}\n	.logo { width: 150px; display:block; margin: 0 auto 15px; }\n	.foot-note {color: #999; font-size:14px;line-height:1;text-align:center;}\n</style>\n<body>\n	<div id=\'all\'>\n<div class=\'section\'>\n			<p>Hi {{name}},<br>Your Staff Account has been successfully created. Here are your details:</p>\n			<div class=\'form-group\'>\n				<strong class=\'login-label\'>Email:</strong> {{email}}\n			</div>\n			<div class=\'form-group\'>\n				<strong class=\'login-label\'>Password:</strong> {{password}}\n			</div>\n			<p>Please go to this link <a href=\'{{link}}\'>account</a> to verify your account and sign in.</p>\n			<br>\n			<p>Sincerely,<br>The CourierPlus Team</p>\n		</div>\n	</div>\n	<div class=\'foot-note\'>&copy; 2015 CourierPlus</div>\n</body>\n</html>', NOW(), 1),
  ('parcel_in_transit', 'dapo@cottacush.com', 'Shipment Movement', 'Hello {{fullname}},<br/><br/>You have authorized the shipments in manifest {{manifest_label}} to be moved from {{origin}} to {{destination}}<br/>Kindly login to see the details of the manifest<br/>Regards,<br/><br/>Operations Unit', NOW(), 1),
  ('parcel_delivered', 'dapo@cottacush.com', 'Delivery Notification', 'Hello {{firstname}},<br/><br/>We are glad to inform you that you shipment with waybill number {{waybill_number}} has been successfully delivered<br/><br/>Kindly contact our support team if there is any question.<br/><br/>Kind regards,<br/><br/>Courier Plus Operations Team', NOW(), 1);
