INSERT INTO `trackplus`.`email_message` (`email_message_code`, `to_email`, `subject`, `message`, `created_date`, `status`) VALUES ('company_api_access_notification', 'depo@cottacush', 'Courier Plus API Access Details', '<!DOCTYPE html>\r\n<html><head>    <title></title></head><style type=\'text/css\'> \r\n   html {    background-color: #ddd}body {    margin: 10px;    color: #666;    font-size: 16px;    line-height: 22px;    \r\n   font-family: sans-serif}#all {    background-color: #fff;    padding: 25px;    margin: 0 0 5px}h1, h2, h3, h4, h5, h6 \r\n   {    color: #333;    margin: 10px 0 15px;    line-height: 1.1}a {    color: #9C27B0}p {    margin-bottom: 10px}header, footer, section \r\n   {    display: block;}strong, b {    font-weight: bold;    color: #333;}.form-group {    margin-bottom: 10px}label \r\n   {    font-weight: bold;    color: #333;    margin-bottom: 2px}.login-label {    width: 90px;}.form-control-static \r\n   {    padding: 3px 0}section, .section {    margin: 8px 0;}.logo {    width: 150px;    display: block;    margin: 0 auto 15px;}.foot-note \r\n   {    color: #999;    font-size: 14px;    line-height: 1;    text-align: center;}</style>\r\n   <body>\r\n   <div id=\'all\'>    <div class=\'section\'>        <p>Dear {{name}},</p>        \r\n   <p>Below is your Courier Plus API access details.</p>        \r\n   <p>DO NOT DISCLOSE.</p>        <br/>\r\n   <div class=\'form-group\'><strong class=\'login-label\'>Registration Number:</strong> {{reg_no}}</div>        <div class=\'form-group\'>\r\n   <strong class=\'login-label\'>Access Token:</strong> {{token}}</div>        <br/>       \r\n   <p>Do provide us feedback on your experience, advice and area of improvement.<br/>        Kind regards,<br/>        \r\n   CourierPlus Team.</p>    </div></div><div class=\'foot-note\'>&copy; 2015 CourierPlus</div></body></html>', '2017-02-28 13:17:05', '1');